import React from 'react';
// import { directive } from '@babel/types';
import UserItem from './UserItem';

const User = ({users, loading}) => {
    return (
      <div style={userStyle}>
        {users.map(user => (
          <UserItem key={user.id} user={user} />
        ))}
      </div>
    );
  }

const userStyle = {
  display: 'grid',
  gridTemplateColumns: 'repeat(3, 1fr)',
  gridGap: '1rem'
};

export default User;
